// Save this file as generate-placeholder-images.js in your project root

const fs = require('fs');
const path = require('path');

// Define directories
const publicImagesDir = path.join(__dirname, 'public', 'images', 'products');

// Create directory if it doesn't exist
if (!fs.existsSync(publicImagesDir)) {
  fs.mkdirSync(publicImagesDir, { recursive: true });
  console.log(`Created directory: ${publicImagesDir}`);
}

// Create a generic placeholder that will work for all products
const placeholderPath = path.join(publicImagesDir, 'placeholder.jpg');
if (!fs.existsSync(placeholderPath)) {
  // Create a simple base64-encoded 1x1 pixel JPEG
  // This is a minimal valid JPEG file that most browsers will recognize
  const minimalJpeg = Buffer.from('', 'base64');
  fs.writeFileSync(placeholderPath, minimalJpeg);
  console.log(`Created generic placeholder image: ${placeholderPath}`);
}

// List of required product images from products.json
const requiredImages = [
  "dell-xps13-1.jpg", "dell-xps13-2.jpg", "dell-xps13-3.jpg",
  "rog-zephyrus-1.jpg", "rog-zephyrus-2.jpg",
  "samsung-980pro-1.jpg", "samsung-980pro-2.jpg",
  "lg-ultragear-1.jpg", "lg-ultragear-2.jpg",
  "intel-i9-12900k-1.jpg", "intel-i9-12900k-2.jpg",
  "rtx-3080-1.jpg", "rtx-3080-2.jpg"
];

// For each required image, create a symbolic link to the placeholder image
for (const filename of requiredImages) {
  const filePath = path.join(publicImagesDir, filename);
  
  if (!fs.existsSync(filePath)) {
    try {
      // Just copy the placeholder image for each required image
      fs.copyFileSync(placeholderPath, filePath);
      console.log(`Created placeholder for: ${filename}`);
    } catch (err) {
      console.error(`Error creating placeholder for ${filename}: ${err.message}`);
    }
  }
}

console.log('\nImage placeholders created successfully!');
console.log('Remember to replace these with actual product images when available.');